params.resourceDescription:
{
  "apiVersion": "kubernetes.crossplane.io/v1alpha2",
  "kind": "Object",
  "metadata": {
    "name": "<ENTITY_NAME>"
  },
  "spec": {
    "forProvider": {
      "manifest": {
        "apiVersion": "v1",
        "kind": "Pod",
        "metadata": {
          "name": "<ENTITY_NAME>",
          "namespace": "default",
          "labels": {
            "name": "<ENTITY_NAME>"
          }
        },
        "spec": {
          "automountServiceAccountToken": false,
          "containers": [
            {
              "name": "busybox-container",
              "image": "busybox:latest",
              "imagePullPolicy": "IfNotPresent",
              "command": [
                "/bin/sh",
                "-c"
              ],
              "args": [
                "while true; do echo 'imalive'; sleep 3600; done"
              ],
              "resources": {
                "limits": {
                  "cpu": "50m",
                  "memory": "64Mi"
                },
                "requests": {
                  "cpu": "10m",
                  "memory": "16Mi"
                }
              }
            }
          ]
        }
      }
    },
    "providerConfigRef": {
      "name": "provider-config-kubernetes"
    }
  }
}
